using System.Web.Mvc;using Microsoft.ApplicationInsights;namespace Cucumber.Web.ErrorHandler{       public sealed class AiHandleErrorAttribte : HandleErrorAttribute    {        public override void OnException(ExceptionContext filterContext)        {            if (filterContext?.HttpContext != null && filterContext.Exception != null)            {                if (filterContext.HttpContext.IsCustomErrorEnabled)                {                    var ai = new TelemetryClient();                    ai.TrackException(filterContext.Exception);                }            }            base.OnException(filterContext);        }    }}