#regionusing System;using System.Web.Mvc;using Microsoft.ApplicationInsights;#endregionnamespace Gpif.Web.ErrorHandler{    [AttributeUsage(AttributeTargets.Class | AttributeTargets.Method, AllowMultiple = true)]    public sealed class AiHandleErrorAttribute : HandleErrorAttribute    {        public override void OnException(ExceptionContext filterContext)        {            if (filterContext?.HttpContext != null && filterContext.Exception != null)            {                if (filterContext.HttpContext.IsCustomErrorEnabled)                {                    var ai = new TelemetryClient();                    ai.TrackException(filterContext.Exception);                }            }            base.OnException(filterContext);        }    }}